function doHorizontalCalculations(cn, keyValue) {
var colShift, colPrefix;
var argCols = [1,9,17,25,33,41,49];
var argPrefix, arg, sum;
for (var col = 57; col <= 64; col++) {
	colShift = col - 57;
	colPrefix = "ka_msfo_apk_kp_array*" + cn[col] + ":num:";
	sum = 0;

	for (var argCol = 0; argCol < argCols.length; argCol++) {
		var argPrefix = "ka_msfo_apk_kp_array*" + cn[argCols[argCol] + colShift] + ":num:";
		var arg = getInputValue(argPrefix + keyValue);
		if (arg != NaN) sum += arg;
	}

	setInputValue(colPrefix + keyValue, sum);
}
}
