function updateCalculatedFields() {
	var A7_1 = Cells("7-1");
	var A11  = Cells("11");
	var A11_1 = Cells("11-1");
	var A12 = Cells("12");
	var A13 = Cells("13");
	var A13_1 = Cells("13-1");
	var A14_3 = Cells("14-3");
	var A15 = Cells("15");
	var A17 = Cells("17");
	var A19 = Cells("19");
	var A20 = Cells("20");
	var A22 = Cells("22");
	var A24 = Cells("24");
	var A26 = Cells("26");
	var tmp;
	// C29
	if (A13_1){ var A14 = A11_1 /A13_1;
	Print("14",A14,3);}

	//C30
	if ((A12+A13)-A7_1){ var A14_1 = A11_1 /((A12+A13)-A7_1);
	Print("14-1",A14_1,3);}

	//C31
	if (((A12+A13)-A7_1)+A14_3){ var A14_2 = A11/(((A12+A13)-A7_1)+A14_3);
	Print("14-2",A14_2,3);}

	//C34
	if (A11){ var A16 = A15/A11;
	Print("16",A16,3);}

	//C36
	if (A11){ var A18 = A17 /A11;
	Print("18",A18,3);}

	//C39
	if (A20){ var A21 = A19 /A20;
	Print("21",A21,3);}

	//C41
	if (A11){ var A23 = A22/A11;
	Print("23",A23,3);}

	//C43
	if (A11){ var A25=A24/A11;
	Print("25",A25,3);}

	//C45
	if (A11){ var A27 = A26 /A11;
	Print("27",A27,3);}
}
function Cells( prefix ) {
var pp = "prud_array*sum:num:"+prefix;
return getInputValue(pp);
}
function ToCellsValue( pref, v){
var pp = "prud_array*sum:num:"+pref;
setInputValue(pp, v);
}
function Print(prefix, v, digit){
if (v) {
		var n=v;
		if (digit === 0)
		  ToCellsValue(prefix,round0(n));
		else if (digit === 1)
		  ToCellsValue(prefix,round1(n));
		else if (digit === 2)
		  ToCellsValue(prefix,round2(n));
		else if (digit === 3)
		  ToCellsValue(prefix,round3(n));
		else if (digit === 4)
		  ToCellsValue(prefix,round4(n));
		else if (digit === 5)
		  ToCellsValue(prefix,round5(n));
		else if (digit === 6)
		  ToCellsValue(prefix,round6(n));
	} else {
		ToCellsValue(prefix,"0");
	}
}
