Form.script:
function updateCalculatedFields() {
var a = [], b = [], c = [];
var sumA = 0, sumC = 0;

for (var i = 1; i <= 68; i++) {
	a[i] = getInputValue("ra_apk_array*sum:num:" + i);
	b[i] = getInputValue("ra_apk_array*deg_risk:num:" + i);
	c[i] = a[i]*b[i]/100;
	if (c[i]) {
	   sumC += c[i];
	   setInputValue("ra_apk_array*sum_calc:num:" + i,c[i]);
	}
	if (a[i]) sumA += a[i];
}
setInputValue("ra_apk_array*sum:num:ItogoRa",sumA);
setInputValue("ra_apk_array*sum_calc:num:ItogoRa",sumC);
}
