Form.script:
function updateCalculatedFields() {
var a = [], b = [], c = [], d = [];
var sumA = 0, sumD = 0;

for (var i = 1; i <= 13; i++) {
a[i] = getInputValue("uvo_apk_array*sum:num:" + i);
b[i] = getInputValue("uvo_apk_array*conv_ration:num:" + i);
c[i] = getInputValue("uvo_apk_array*conv_credrisk:num:" + i);
d[i] = a[i]*b[i]/100;
if (d[i]) {
   setInputValue("uvo_apk_array*sum_calc:num:" + i,d[i]);
   sumD += d[i];
} else{
	setInputValue("uvo_apk_array*sum_calc:num:" + i, "0");
}

sumA += a[i];
}
setInputValue("uvo_apk_array*sum:num:ItogoUvo",sumA);
setInputValue("uvo_apk_array*sum_calc:num:ItogoUvo",sumD);
}
