Form.script:
function updateCalculatedFields() {
var a=[],b=[],c=[];
var SumA=0, SumB=0;

for (var i = 1; i <= 11; i++) {
	a[i] = getInputValue("uvo_ot_array*sum:num:"+i);
	b[i] = getInputValue("uvo_ot_array*conv_ration:num:"+i);
	c[i] = a[i]*b[i]/100;
	if (c[i]) {
		SumB += c[i];
		}
else
		{
			c[i] = 0;
		}
	SumA += a[i];
	setInputValue("uvo_ot_array*sum_calc:num:" + i,c[i]);
}

setInputValue("uvo_ot_array*sum:num:ItogoUvo",SumA);
setInputValue("uvo_ot_array*sum_calc:num:ItogoUvo",SumB);
}
