// Суммирование n строк, которые внизу
function SummaryRowDown(cn,keyValue, n){
var sum=0;
for ( var acol=1; acol<=56; acol++) {
	for (var arow=1; arow<=n; arow++) {
		var argPrefix = "zpd_msfo_apk_array*" + cn[acol] + ":num:";
		var fullPrefix = argPrefix + keyValue+"."+ arow
		var arg = getInputValue(fullPrefix);
		sum += arg;
		}
	setInputValue(argPrefix + keyValue,sum);
	sum = 0;
}
}
function doHorizontalCalculations(cn, keyValue) {
var colShift, colPrefix;
var argCols = [1,9,17,25,33,41,49];
var argPrefix, arg, sum;
for (var col = 57; col <= 64; col++) {
	colShift = col - 57;
	colPrefix = "zpd_msfo_apk_array*" + cn[col] + ":num:";
	sum = 0;
	for (var argCol = 0; argCol < argCols.length; argCol++) {
		var argPrefix = "zpd_msfo_apk_array*" + cn[argCols[argCol] + colShift] + ":num:";
		var arg = getInputValue(argPrefix + keyValue);
		sum += arg;
	}
	setInputValue(colPrefix + keyValue,sum);
}
}
